from email.mime.text import MIMEText
from email.mime.multipart import MIMEMultipart

def sslCheck(hostname):
    currentHostName="%s"%(hostname)
    #print("%s"%(hostname))
    ssl_date_fmt = r'%b %d %H:%M:%S %Y %Z'
    context = ssl.create_default_context()
    conn = context.wrap_socket(socket.socket(socket.AF_INET),server_hostname=hostname,)
    conn.settimeout(3.0)
    conn.connect((hostname, 443))
    ssl_info = conn.getpeercert()
    Exp_ON=datetime.datetime.strptime(ssl_info['notAfter'], ssl_date_fmt)
    Days_Remaining= Exp_ON - datetime.datetime.utcnow()
    currentExpiresOn="Expires ON:- %s"%(Exp_ON)
    currentRemaining="Remaining:- %s" %(Days_Remaining)
    currentLine= "----------------------------------"

    sslInfo = dict()
    sslInfo["line"]=currentLine
    sslInfo["hostname"]=currentHostName
    sslInfo["expires"]=currentExpiresOn
    sslInfo["remaining"]=currentRemaining

    return sslInfo


